package org.santihs.poointerfaces.printing.model;import java.util.ArrayList;import java.util.List;public class Book implements Printable{    private List<Printable> pages;    private String author;    private String title;    private Gender gender;    public Book(String author, String title, Gender gender) {        this.pages = new ArrayList<>();        this.author = author;        this.title = title;        this.gender = gender;    }    public Book addPage(Printable printable){        pages.add(printable);        return this;    }    @Override    public String print() {        final StringBuilder sb = new StringBuilder("Book{");        sb.append("author='").append(author).append("\n");        sb.append("title='").append(title).append("\n");        sb.append("gender=").append(gender).append("\n");        sb.append("pages [");        for (Printable page :                this.pages) {            sb.append(page.print()).append(", ");        }        sb.append("]");        sb.append('}');        return sb.toString();    }}