package org.santihs.pooexceptions.example;public class Calculator {        public double division(double dividend, double divisor) throws DivisionByZeroException {            if(dividend == 0)                throw new DivisionByZeroException("/ by zero siuu");            return dividend / divisor;        }        public double division(String dividend, String divisor) throws DivisionByZeroException,                FormatNumberException {            try {                double numberDividend = Integer.parseInt(dividend);                double numberDivisor = Integer.parseInt(divisor);                return this.division(numberDividend, numberDivisor);            } catch (NumberFormatException fe){                throw new FormatNumberException("Problem numberException");            }        }}