import java.lang.reflect.Method;public class GetClassMethod {    public static void main(String[] args) {        String text = "Hi what up";        Class<? extends String> textClass = text.getClass();        System.out.println("textClass.getName() = " + textClass.getName());        System.out.println("textClass.getName() = " + textClass.getSimpleName());        System.out.println("textClass.getName() = " + textClass.getPackageName());        /* for (Method method : textClass.getMethods()) {            System.out.println("method = " + method.getName());        } */        Integer number  = 34;        Class intClass = number.getClass();        System.out.println("intClass = " + intClass.getSimpleName());        System.out.println("intClass = " + intClass.getSuperclass().getSimpleName());    }}