public class ForReference {    public static void main(String[] args) {        int[] ages = {10, 11, 12};        for (int i : ages) {            System.out.println("age variable = " + i);        }        test(ages);        for (int i : ages) {            System.out.println("age variable = " + i);        }    }    private static void test(int[] ages){        ages[0] = 15;        ages[1] = 16;        ages[2] = 17;        System.out.println("Initialized test method");        for (int i : ages) {            System.out.println("Age variable = " + i);        }        System.out.println("Finished test method");    }}