public class ForReferenceObjectPerson {    public static void main(String[] args) {        Persona persona = new Persona();        System.out.println("Initialized person");        System.out.println("persona = " + persona.getName());        test(persona);    }    private static void test(Persona persona){        persona.setName("Santihs");        System.out.println("persona = " + persona.getName());    }}